import React, { useState, useEffect } from "react";
import "./Chat.scss";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8080");
    setSocket(ws);

    // Recibir mensajes del servidor
    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data); // Asume que es JSON, ajusta si no es el caso
        setMessages((prevMessages) => [...prevMessages, data]);
      } catch (e) {
        console.error("Error parsing message:", e);
      }
    };

    // Cerrar conexión cuando se desmonta el componente
    return () => ws.close();
  }, []);

  /**
   * Envía un mensaje mediante una conexión de socket, si el socket está disponible.
   * @param message: es el contenido que se transmitirá.
   */
  const sendMessage = (message) => {
    if (socket) {
      socket.send(message);
    }
  };

  return (
    <div className="chat">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            {msg}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Escribe un mensaje..."
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            sendMessage(e.target.value);
            e.target.value = "";
          }
        }}
      />
    </div>
  );
};

export default Chat;
