import React, { useState, useEffect, useRef } from "react";
import "./Chat.scss";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = new WebSocket("ws://localhost:8080");

    // Cuando se abre la conexión
    socketRef.current.onopen = () => {
      console.log("Conexión WebSocket establecida");
    };


    // Manejar mensajes entrantes
    socketRef.current.onmessage = async (event) => {
      try {
        // let messageData =
        //   event.data instanceof Blob ? await event.data.text() : event.data;
        // const parsedData = JSON.parse(messageData);
        // setMessages((prevMessages) => [...prevMessages, parsedData]);

        let messageData;
        // Verifica si el mensaje es un Blob
        if (event.data instanceof Blob) {
          // Convierte el Blob a texto
          messageData = await event.data.text();
        } else {
          // Si no es Blob, usa el dato directamente
          messageData = event.data;
        }

        // Verificamos si el mensaje es un JSON válido
        try {
          const parsedData = JSON.parse(messageData);
          setMessages((prevMessages) => [...prevMessages, parsedData]);
        } catch (e) {
          // Si no es JSON, lo tratamos como texto plano
          setMessages((prevMessages) => [...prevMessages, messageData]);
        }
      } catch (e) {
        console.error("Error parsing message:", e);
      }
    };

    // Limpiar la conexión cuando el componente se desmonte
    return () => {
      if (socketRef.current) socketRef.current.close();
    };
  }, []);

  /**
   * Envía un mensaje mediante una conexión de socket, si el socket está disponible.
   * @param message: es el contenido que se transmitirá.
   */
  const sendMessage = (message) => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send(message);
    } else {
      console.warn("WebSocket no está listo para enviar mensajes.");
    }
  };

  return (
    <div className="chat">
      <div className="messages">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${
              msg.source === "server" ? "server" : "client"
            }`}
          >
            {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Escribe un mensaje..."
        onKeyDown={(e) => {
          if (e.key === "Enter" && e.target.value.trim() !== "") {
            sendMessage(e.target.value);
            e.target.value = "";
          }
        }}
      />
    </div>
  );
};

export default Chat;
